mapper.update:
UPDATE POSTS
SET
POST_VIEWS=${views}
where
post_id=#{postId}

UPDATE POSTS
SET
POST_LIKES=#{postLikes}
where
post_id=#{postId}

mapper.select:
SELECT COUNT(*)
FROM LIKES
WHERE
POST_ID = #{postId}
AND
USER_ID = #{userId}

mapper.update:
